import 'package:flutter/material.dart';import 'presentation/task.dart';class FirstScreen extends StatelessWidget {  var cameras;  FirstScreen(this.cameras);  @override  Widget build(BuildContext context) {    return Scaffold(      body: SingleChildScrollView(child: Container(        height: MediaQuery.of(context).size.height,        width: MediaQuery.of(context).size.width,        decoration: BoxDecoration(          color: Colors.yellow,          gradient: LinearGradient(            begin: Alignment.center,            end: Alignment(                10, -7), // 10% of the width, so there are ten blinds.            colors: [              Colors.greenAccent[400],              Colors.black54            ],            tileMode: TileMode.repeated, // repeats the gradient over the canvas          ),        ),        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          crossAxisAlignment: CrossAxisAlignment.center,          children: [            Text('Yellow Class',style: TextStyle(fontWeight: FontWeight.bold,fontSize: 50,color: Colors.black,letterSpacing: 20),),            SizedBox(height: MediaQuery.of(context).size.height*.10,),            FlatButton(                onPressed: () {Navigator.of(context).push(MaterialPageRoute(builder: (context) => Task(cameras),));},                color: Colors.lightGreen,                child: Text('Task')),          ],        ),      )),    );  }}