import 'package:flutter/material.dart';import 'package:camera/camera.dart';class CamHomePage extends StatefulWidget {  List<CameraDescription> cameras;  CamHomePage(this.cameras);  @override  _CamHomePageState createState() => _CamHomePageState();}class _CamHomePageState extends State<CamHomePage> {  CameraController controller;  void initState() {    super.initState();    controller = CameraController(widget.cameras[1], ResolutionPreset.high);    controller.initialize().then((_) {      if (!mounted) {        return;      }      setState(() {});    });  }  @override  void dispose() {    controller?.dispose();    super.dispose();  }  bool isExpandedView = false;  @override  Widget build(BuildContext context) {    if (!controller.value.isInitialized) {      return Center(child: Container(        child: CircularProgressIndicator(),      ));    }    return Container(      child: ClipRRect(        //borderRadius: BorderRadius.circular(15.0),        child: CameraPreview(controller),      ),    );  }}