import 'package:flutter/material.dart';import 'package:yellow_class/presentation/custom/camera.dart';import 'custom/player.dart';class Task extends StatefulWidget {  var cameras;  Task(this.cameras);  @override  _TaskState createState() => _TaskState();}class _TaskState extends State<Task> {  var top = 200.0;  var left = 300.0;  @override  Widget build(BuildContext context) {    return Scaffold(body: Stack(      children: [        Player(),        Positioned(          top: top,          left: left,          child: GestureDetector(              onVerticalDragUpdate: (DragUpdateDetails dd){                print(dd);                setState(() {                  top = dd.localPosition.dy;                  left = dd.localPosition.dx;                });              },              child: Container(                height: 70,                width: 50,                color: Colors.amber,                child: CamHomePage(widget.cameras),              )),        )      ],    ));  }}